function checkKey(e){e=e||window.event;["38","40","37","39"].includes(e.keyCode)&&e.preventDefault(),"38"==e.keyCode?console.log("up"):"40"==e.keyCode?console.log("down"):"37"==e.keyCode?console.log("left"):"39"==e.keyCode&&console.log("right")}document.addEventListener("keydown",(function(e){checkKey(e)}));